import React, { useState } from 'react';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Search from './components/Search';
import PropertyCard from './components/PropertyCard';
import Modals from './components/Modals';
import './App.css';

const App = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);
    const [showPostProperty, setShowPostProperty] = useState(false);

    const handleLoginClick = () => setShowLogin(true);
    const handleRegisterClick = () => setShowRegister(true);
    const handlePostPropertyClick = () => setShowPostProperty(true);
    const handleCloseModal = () => {
        setShowLogin(false);
        setShowRegister(false);
        setShowPostProperty(false);
    };

    return (
        <div>
            <Header onLoginClick={handleLoginClick} onRegisterClick={handleRegisterClick} />
            <Carousel />
            <Search />
            <div className="properties-grid">
                {/* Map through properties and render PropertyCard components */}
                {/* Example: properties.map(property => <PropertyCard key={property.id} property={property} />) */}
            </div>
            <Modals 
                showLogin={showLogin} 
                showRegister={showRegister} 
                showPostProperty={showPostProperty} 
                onClose={handleCloseModal} 
            />
        </div>
    );
};

export default App;
