import React, { useState } from 'react';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Search from './components/Search';
import PropertyList from './components/PropertyList';
import Modals from './components/Modals';
import './App.css';

const App = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [showPostProperty, setShowPostProperty] = useState(false);
  const [properties, setProperties] = useState([
    {
      id: 1,
      name: "Modern Downtown Apartment",
      description: "Beautiful modern apartment in the heart of downtown.",
      address: "123 Main St, Cityville",
      type: "apartment",
      price: 1500,
      sqft: 850,
      image: "https://placehold.co/600x400?text=Modern+Apartment"
    },
    {
      id: 2,
      name: "Cozy Suburban House",
      description: "Charming 3-bedroom house in a quiet neighborhood.",
      address: "456 Oak Ave, Suburbia",
      type: "house",
      price: 2000,
      sqft: 1200,
      image: "https://placehold.co/600x400?text=Cozy+House"
    }
  ]);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = () => setShowLogin(true);
  const handleRegister = () => setShowRegister(true);
  const handlePostProperty = () => setShowPostProperty(true);
  const closeModal = () => {
    setShowLogin(false);
    setShowRegister(false);
    setShowPostProperty(false);
  };

  const addProperty = (newProperty) => {
    setProperties([...properties, {
      ...newProperty,
      id: properties.length + 1,
      price: parseInt(newProperty.price),
      sqft: parseInt(newProperty.sqft)
    }]);
    setShowPostProperty(false);
  };

  return (
    <div className="App">
      <Header 
        onLogin={handleLogin} 
        onRegister={handleRegister} 
        onPostProperty={handlePostProperty}
        currentUser={currentUser}
      />
      <Carousel />
      <Search />
      <PropertyList properties={properties} />
      <Modals
        showLogin={showLogin}
        showRegister={showRegister}
        showPostProperty={showPostProperty}
        onClose={closeModal}
        onAddProperty={addProperty}
        onLoginSuccess={(user) => setCurrentUser(user)}
      />
    </div>
  );
};

export default App;
