import React from 'react';

const Modals = ({ showLogin, showRegister, showPostProperty, onClose }) => {
    return (
        <>
            {showLogin && (
                <div className="modal" id="login-modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2>Login</h2>
                            <button className="close-button" onClick={onClose}>&times;</button>
                        </div>
                        <form id="login-form">
                            {/* Login form fields */}
                        </form>
                    </div>
                </div>
            )}
            {showRegister && (
                <div className="modal" id="register-modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2>Register</h2>
                            <button className="close-button" onClick={onClose}>&times;</button>
                        </div>
                        <form id="register-form">
                            {/* Registration form fields */}
                        </form>
                    </div>
                </div>
            )}
            {showPostProperty && (
                <div className="modal" id="post-property-modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2>Post a Property</h2>
                            <button className="close-button" onClick={onClose}>&times;</button>
                        </div>
                        <form id="property-form">
                            {/* Property posting form fields */}
                        </form>
                    </div>
                </div>
            )}
        </>
    );
};

export default Modals;
